import sailpoint.tools.Util;

String userID = identity.getName();
String first = identity.getFirstname();
String last = identity.getLastname();
if ((null != Util.getString(first)) && (null != Util.getString(last))) {
    userID = first.toLowerCase().charAt(0) + last.toLowerCase();
}
return userID;


String first = identity.getFirstname();
String last = identity.getLastname();
if ( last == null )
    last = identity.getName();

String alias = null;
int max = 7;
if ( last.length() < max ) {
    max = last.length();
}

if ( first != null ) {
    alias = first.charAt(0) + last.substring(0,max);
} else {
    if ( last.length() < 8 )
        max = last.length();
    alias = last.substring(0,max);
}
return alias;


