import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.object.ProvisioningPlan.AttributeRequest;
import sailpoint.object.ProvisioningPlan.ObjectOperation;
import sailpoint.object.ProvisioningPlan.ObjectRequest;
import sailpoint.api.SailPointContext;
import sailpoint.object.Application;
import sailpoint.object.Identity;
import sailpoint.object.Link;
import sailpoint.tools.Util;
import sailpoint.object.ProvisioningPlan;


List accounts = plan.getAccountRequests();
if (accounts != null) 
{
  for (AccountRequest req : accounts) 
  {
    AccountRequest.Operation op = req.getOperation();

    if (op != AccountRequest.Operation.Create)
    {
      // find the Link for this account
      Application app = context.getObject(Application.class, req.getApplication());
      if(app != null)
      {
        if(app.getProxy() != null) 
        {
            Link link = identity.getLink(app, req.getNativeIdentity());
            if (link == null) 
            {
                // should never happen, on an Operation.Delete
                // print("Missing link!");
            }
            else 
            {
                String attname = "mvaccountName";
                Object value = link.getAttribute(attname);
                AttributeRequest att = new AttributeRequest(attname, value);
                req.add(att);
                String mvcsDNAtt = "mvcsDN";
                Object value = link.getAttribute(mvcsDNAtt);
                AttributeRequest mvcsAtt = new AttributeRequest(mvcsDNAtt, value);
                req.add(mvcsAtt);
            }
        } else {
            Link link = identity.getLink(app, req.getNativeIdentity());
            if (link == null) 
            {
                // should never happen, on an Operation.Delete
                // print("Missing link!");
            } 
            else 
            {
                String attname = "accountName";
                Object value = link.getAttribute(attname);
                AttributeRequest att = new AttributeRequest(attname, value);
                req.add(att);
            }
        }
      }
    }
  }
}

import java.util.*;
import sailpoint.object.ProvisioningPlan.ObjectOperation;
import sailpoint.object.ProvisioningPlan.ObjectRequest;
import sailpoint.object.ProvisioningPlan.AttributeRequest;
import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.object.ProvisioningPlan.GenericRequest;
import sailpoint.api.*;
import sailpoint.object.*;
import sailpoint.tools.*;

List accountRequests = plan.getAccountRequests();
if (accountRequests  != null) {
for (AccountRequest acctReq : accountRequests){
     AccountRequest.Operation op = acctReq.getOperation();
     if (op == AccountRequest.Operation.Create){
         String userId = acctReq.getNativeIdentity();
         if (userId != null){
            acctReq.setNativeIdentity(userId.toUpperCase());
         }
         AttributeRequest USER_OE_PR = acctReq.getAttributeRequest("USER_OE_PR");
         if (USER_OE_PR != null){
            String strUSER_OE_PR = USER_OE_PR.getValue();
            USER_OE_PR.setValue(strUSER_OE_PR.toUpperCase());
         }
     }
     SailPointContext context=SailPointFactory.getCurrentContext();
     if(context != null)
     {
       IdentityService identityService = new IdentityService(context);
       Application app = context.getObject(Application.class, acctReq.getApplicationName());
       if(app != null)
       {
         String shouldUseMSAdminDefined = (String) app.getAttributeValue("useMSAdminDefined");
         String msAdmin=null;
         if(Util.isNotNullOrEmpty(shouldUseMSAdminDefined)) 
         {
           if(shouldUseMSAdminDefined.equals("Y"))
           {
             List idnList = plan.getRequesters();
             if(idnList!=null)
             {
               if(!idnList.isEmpty())
               {
                 Identity idn=(Identity) idnList.get(0);
                 List lnkList = identityService.getLinks(idn, app);
                 if(lnkList!=null)
                 {
                   if(!lnkList.isEmpty())
                   {
                     Link lnk = (Link) lnkList.get(0);
                     //System.out.println("Adding msAdmin in provisioning plan.");
                     acctReq.addArgument("msAdmin", lnk.getDisplayableName());
                   }
                 }
                 else
                   System.out.println("Requester does not have a link from application " + app.getName());
               }
             }
             else
               System.out.println("No requester found.");
           }
         }
         //else
           //System.out.println("shouldUseMSAdminDefined is not set to Y");
       }
     }
  }
}
