import sailpoint.api.*;
import sailpoint.tools.*;
import sailpoint.object.*;
import java.util.*;
import java.text.*;

Identity identity = context.getObjectByName(Identity.class, identityName);
String uid = "";
if( identity.getAttribute("uid") != null )
{
	uid = identity.getAttribute("uid").toString().trim() ;
}

if(identity.getAttribute("uid") == null || uid.isEmpty()){
	return true;
}

return false;

import sailpoint.api.*;
import sailpoint.tools.*;
import sailpoint.object.*;
import java.util.*;
import java.text.*;
import sailpoint.server.Auditor;

System.out.println("New user was entered the system: " + event.getObject().getDisplayableName());

String newUID = getNewUID(context);

if (Auditor.isEnabled("JoinerEvent")) {
	Auditor.logAs("Joiner-WF-AssignUID", "JoinerEvent", identityName, newUID, "", "", "");
	context.commitTransaction();
}

Identity identity = context.getObjectByName(Identity.class, identityName);
identity.setAttribute("uid", newUID);
context.saveObject(identity);
context.commitTransaction();

System.out.println(Generated new uid for " + identityName + " UID: " + newUID);

return plan;

